<polymer-element name="ts-clock">
    <template bind="{{clock}}">
        <style>
            :host .clock{
                display: inline-flex;
                justify-content: space-around;
                background: floralwhite;

                font-size: 2rem;
                font-family: serif;
            }
            :host .clock .hour,:host .clock .minute, :host .clock .second{
                color: tomato;
                padding: 1.5rem;
                text-shadow: 0px 1px grey;
            }
        </style>
 <div class="clock">
 <div class="hour">{{ clock.hour }}</div>
 <div class="minute">{{ clock.minute }}</div>
 <div class="second">{{ clock.second }}</div>
 </div>
    </template>
    <script>
        Polymer({
 ready:function(){
 this.updateClock()
 },
 updateClock: function(){
 var date = new Date();
 this.clock ={
 hour : date.getHours(),
 minute : date.getMinutes(),
 second : date.getSeconds()
 };
 this.async(this.updateClock, null, 1000);
 }
        });
    </script>
</polymer-element>
